import { Button, Checkbox, FormControlLabel, FormGroup, IconButton, ListItem } from '@material-ui/core';
import React from 'react'
import { useDispatch } from 'react-redux';
import { TOGGLE_COMPLETED } from '../redux/actions';
import DeleteIcon from '@material-ui/icons/Delete';



const TodoItem = ({value}) => {
    const dispatch = useDispatch();

    const {id, title, completed, important} = value;
    const toggleCheckbox=()=>{
        dispatch({
            type:TOGGLE_COMPLETED,
            payload:{
                id
            }

        })
    }

    const handleDelete=()=>{
        
    }

    return (
    <React.Fragment>
        <ListItem>
            <FormGroup>
                <FormControlLabel control={<Checkbox checked={completed} onChange={toggleCheckbox} name={title} />} label={title} />
            </FormGroup>
            <IconButton aria-label="delete" size="large" edge="end" onClick={handleDelete}>
                <DeleteIcon fontSize="inherit" />
            </IconButton>

        </ListItem>
    </React.Fragment>
  )
}

export default TodoItem
