import { useEffect } from "react";
import { connect } from "react-redux";
import { getAllCategories } from "./actions/categoriesActions";
import { getProducts } from "./actions/productsActions";
import Header from "./components/Header";
import SelectDropdown from "./components/SelectDropdown";
produ




function App({categories,dispatch}) {
  useEffect(()=>{
    dispatch(getAllCategories())
  },[dispatch])

  const hadleCategoryChange=(event)=>{
    const category = event.target.value.trim();
    if(category){
      dispatch(getProducts(category))
    }
  }

  return (
    <div>
      <Header/>
      <div className="category-dropdown">
        <SelectDropdown categories={categories}
        handleCategoryChange={hadleCategoryChange}
        />
      </div>
      <div>
  <ul className="products">
    {products.map(({ name, image }) => {
      return <Product key={name} name={name} image={image} />;
    })}
  </ul>
</div>
    </div>
  );
}

const mapStateToProps=(state)=>{
  const {categories,products} = state;
  return {
    categories,
    products
  }
}

export default connect(mapStateToProps)(App);
