import shortid from "shortid";
import { ADD_TODO } from "./actions";
import { todos } from "./initial-state";
toggle




function reducer(state=todos,action){
    switch (action.type) {
        case ADD_TODO:
            const {title} = action.payload;
            const newTodo={
                id:shortid(),
                title,
                completed:false,
                important:false
            }

            return [...state,newTodo];
    
        default:
            return state;
    }    
}

export default reducer;
