import {
    Button,
    Checkbox,
    FormControlLabel,
    FormGroup,
    IconButton,
    ListItem
  } from "@material-ui/core";
  import React from "react";
  import { useDispatch } from "react-redux";
  import { DELETE_TODO, TOGGLE_COMPLETED } from "../redux/actions";
  import DeleteIcon from "@material-ui/icons/Delete";
  import EditIcon from "@material-ui/icons/Edit";
  import StarIcon from "@material-ui/icons/Star";
  import StarOutlineIcon from "@material-ui/icons/StarOutline";
  
  const TodoItem = ({ value }) => {
    const dispatch = useDispatch();
    const { id, title, completed, important } = value;
    const toggleCheckbox = () => {
      dispatch({
        type: TOGGLE_COMPLETED,
        payload: {
          id
        }
      });
    };
  
    const handleDelete = () => {
      dispatch({
        type: DELETE_TODO,
        payload: {
          id
        }
      });
    };
  
    const toggleImport = () => {
      dispatch({
        type: "TOGGLE_IMPORTANT",
        payload: {
          id
        }
      });
    };
  
    return (
      <React.Fragment>
        <ListItem style={{ display: "flex", justifyContent: "space-between" }}>
          <FormGroup style={{ marginRight: "auto" }}>
            <FormControlLabel
              control={
                <Checkbox
                  checked={completed}
                  color="primary"
                  onChange={toggleCheckbox}
                  name={title}
                />
              }
              label={title}
            />
          </FormGroup>
          <IconButton edge="end" onClick={toggleImport}>
            <EditIcon /> 
          </IconButton>
          <IconButton edge="end" onClick={toggleImport}>
            {important ? <StarIcon /> : <StarOutlineIcon />}
          </IconButton>
          <IconButton
            color="secondary"
            aria-label="delete"
            size="large"
            edge="end"
            onClick={handleDelete}
          >
            <DeleteIcon fontSize="inherit" />
          </IconButton>
        </ListItem>
      </React.Fragment>
    );
  };
  
  export default TodoItem;
