const App = () => {
  const title = useRef();
  console.log(title);

  const fetchData=(e)=>{

    e.preventDefault();
    const txttitle = title.current.value;
    console.log(txttitle);

  }

  return (
    <React.Fragment>
      <form onSubmit={fetchData}>
        <input ref={title} type="text" />
      </form>

      <button>add</button>
    </React.Fragment>
  )
}

export default App
