import { editText } from "./editText";
import { EDIT_TODO } from "./utils";
function editreducer(state=editText,action){
    
    switch(action.type){
        case EDIT_TODO:{
            const {id} = action.payload;
            return state=id

        }
    
        default:
            return state;
    }

}


export default editreducer;
