import React from "react";
import { Form, Button } from "react-bootstrap";
import {useFormik} from 'formik'
import { signUpSchema } from "../schemas";


const Registration = () => {

    const initialValues={
        name:'',
        email:'',
        password:'',
        confirm_password:''
    }


    const {values, action,errors, handleBlur, handleChange, handleSubmit, touched} = useFormik({
        initialValues:initialValues,
        validationSchema:signUpSchema,
        onSubmit:(values,action)=>{
            console.log('values are',values);
            action.resetForm();
        }
        
    })

    console.log(errors);


  return (
    <React.Fragment>
      <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Name</Form.Label>
          <Form.Control autoComplete="off" value={values.name} type="text" name="name" placeholder="Enter name" onChange={handleChange} onBlur={handleBlur}/>
          {errors.name && touched.name ? <p className="form-error">{errors.name}</p>:null}
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control autoComplete="off" type="email" value={values.email}  name="email" placeholder="Enter email" onChange={handleChange} onBlur={handleBlur} />
          {errors.email && touched.email ? <p className="form-error">{errors.email}</p>:null}
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control autoComplete="off" type="password" value={values.password}  name="password" placeholder="Password"  onChange={handleChange} onBlur={handleBlur}/>
          {errors.password && touched.password ? <p className="form-error">{errors.password}</p>:null}
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control autoComplete="off" type="password" value={values.confirm_password}  name="confirm_password" placeholder="Confirm Password"  onChange={handleChange} onBlur={handleBlur}/>
          {errors.confirm_password && touched.confirm_password ? <p className="form-error">{errors.confirm_password}</p>:null}
        </Form.Group>

        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
    </React.Fragment>
  );
};

export default Registration;
