import React, { useMemo, useState } from 'react'
import Test1 from './Test1';


const Card = () => {

    const [num, setNum]=useState(0);
    console.log('component re-render');

    const incrementVal=useMemo(()=>{
        setNum((preVal)=>{
            return preVal+1;
        })
    },[num])

  return (
    <div>
        <p>{num}</p>
        <button onClick={incrementVal}>click here</button>
        <Test1/>
    </div>
  )
}

export default Card
