import { ADD_TODO, DELETE_TODO, EDIT_TODO, TOGGLE_COMPLETED, TOGGLE_IMPORTANT } from "./utils";

export const addTodo=(title)=>{
    return {
        type:ADD_TODO,
        payload:{
            title
        }
    }
}


export const toggleCompleted=(id)=>{
    return {
        type:TOGGLE_COMPLETED,
        payload:{
            id
        }
    }
}


export const deleteTodo=(id)=>{
    return {
        type:DELETE_TODO,
        payload:{
            id
        }
    }
}

export const toggle=(id)=>{
    return {
        type:TOGGLE_IMPORTANT,
        payload:{
            id
        }
    }
}
