import { Box, Button, TextField } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addTodo } from "../redux/actions";


const TodoAdder = () => {
  const [title, setTitle] = useState("");
  const handleTextChange = (e) => {
    setTitle(e.target.value);
  };

  const dispatch = useDispatch();

  const addtodoItem = () => {
    dispatch(addTodo(title));
    setTitle("");
  };

  const editId = useSelector((state) => {
    return state.editreducer;
  });


  const editTodo = useSelector((state) => {
    return state.todoreducer;
  });


//   let editList;
//   console.log('calling todoAdder');
//   useEffect(()=>{
//     console.log('---calling useeffect---');
//     console.log('edit id is',editId);      
//     editList = editTodo.filter((val,idx)=>{
//         return val.id===editId;
//     })
//   },[editId])

(function () {
    const editList = editTodo.filter((val,idx)=>{
        return val.id===editId;
    })
    setTitle(editList.title);
})();



  return (
    <React.Fragment>
      <Box>
        <TextField
          style={{ width: 400 }}
          id="standard-textarea"
          label="add todo"
          placeholder="text..."
          multiline
          variant="standard"
          value={title}
          onChange={handleTextChange}
        />
        <Button
          style={{ height: 45 }}
          variant="contained"
          onClick={addtodoItem}
        >
          add
        </Button>
      </Box>
    </React.Fragment>
  );
};

export default TodoAdder;
