import {createStore} from 'redux';

const reducer=(state=1,action)=>{
    switch(action.type){
        case 'INCREMENT':
            return state+action.payload;
        case 'DECREMENT':
            return state-action.payload;
        default:
            return state;
    }
}

const store = createStore(reducer);

store.subscribe(()=>{
    console.log('current state is ',store.getState());
})

dispatch({
    type:'INCREMENT',
    payload:1
});

dispatch({
    type:'DECREMENT',
    payload:2
});

export default store;
