import { Box, Grid, makeStyles,Typography,Divider, ListItem, ListItemText, List } from '@material-ui/core'


import React from 'react'
import { useSelector } from 'react-redux';
import TodoItem from './TodoItem';

const useStyles = makeStyles((theme)=>({
    root:{
        margin:20,
        padding:20,
        backgroundColor:'rgb(92.9%,92.9%,92.9%)'
    }

}))


const TodoContainer = () => {
    const classes = useStyles();

    const todos = useSelector((state)=>{
        console.log("before new",state);
        return{
            todos:state
        }
    })

    console.log('todocontainer');
    console.log(todos);
    console.log('---------------');
  return (
        <Box className={classes.root}>
            <Grid container spacing={2}>
                <Grid item xs={6}>
                    <Typography variant="h5" gutterBottom style={{textAlign:'left'}}>
                        Todo list
                    </Typography>
                    <Divider />
                    <List>
                        {
                            todos.map((value)=>{
                                if(!value.completed){
                                    return <TodoItem key={value.id} value={value}></TodoItem>
                                }else{
                                    return false;
                                }
                            })
                        }
                    </List>


                </Grid>
                <Grid item xs={6}>
                    <Typography variant="h5" gutterBottom style={{textAlign:'left'}}>
                        completed
                    </Typography>
                    <Divider />
                    <List>
                        {
                            todos.map((value)=>{
                                if(value.completed){
                                    return <TodoItem key={value.id} value={value}></TodoItem>
                                }else{
                                    return false;
                                }
                            })
                        }
                    </List>
                </Grid>
            </Grid>
        </Box>
  )
}

export default TodoContainer
