import { Button, Checkbox, FormControlLabel, FormGroup, ListItem } from '@material-ui/core';
import React from 'react'
import { useDispatch } from 'react-redux';
import { TOGGLE_COMPLETED } from '../redux/actions';
Button



const TodoItem = ({value}) => {
    const dispatch = useDispatch();

    const {id, title, completed, important} = value;
    const toggleCheckbox=()=>{
        dispatch({
            type:TOGGLE_COMPLETED,
            payload:{
                id
            }

        })
    }

    return (
    <React.Fragment>
        <ListItem>
            <FormGroup>
                <FormControlLabel control={<Checkbox checked={completed} onChange={toggleCheckbox} name={title} />} label={title} />
                <Button variant="outlined" startIcon={<DeleteIcon />}>
                    Delete
                </Button>
            </FormGroup>
        </ListItem>
    </React.Fragment>
  )
}

export default TodoItem
