import { CREATE_TODO, REMOVE_TODO } from "./actions";


export const todos=(state=[],action)=>{
    const {payload, type} = action;

    switch(type){
        case CREATE_TODO:{
            const {text} = payload;
            const newTodo={
                text,
                isCompleted:false
            }
            return [...state,text]
        }
        case REMOVE_TODO:
            return state.filter(value=>value.text!==payload.text)  
        
        default:
            return state;
                    
    }
}
