    <div className='new-todo-form'>
        <input placeholder="type your new todo here" className='new-todo-input' type="text" value={inputValue} onChange={e=>setInputValue(e.target.value)}/>
        <button className='new-todo-button'>create todo</button>
    </div>
  )
}

const mapStateToProps=state=>({
  todo
});
