export const contactReducer=(state=[],action)=>{
    switch(action.type){
        case 'ADD_CONTACT':
            return [...state,action.contact];

        case 'DELETE_CONTACT':
            return state.filter((value)=>value.id!=action.id);
        default:
            return state;
        
    }
}
