import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { v4 as uuidv4 } from "uuid";
import { addContact } from '../redux/actions';



const ContactForm = () => {
  const dispatch = useDispatch();
  const navigate= useNavigate();

  const [contact,setContact]=useState({
    fname:'',
    lname:'',
    phone:''
  })

  const handleForm=(e)=>{
    const {name,value}=e.target;
    setContact((oldValue)=>{
      return {
        ...oldValue,
        [name]:value
      }
    })
  }

  const handleOnSubmit=(e)=>{
    e.preventDefault();
    const {fname,lname,phone} = contact;
    console.log(contact);
    dispatch(addContact({
      id:uuidv4(),
      fname,
      lname,
      phone
    }))
    navigate("/list");




  }

  return (
    <React.Fragment>
      <form onSubmit={handleOnSubmit}>
        <p>
          <input type="text" name="fname" value={contact.fname} onChange={handleForm}/>
        </p>
        <p>
          <input type="text" name="lname"  value={contact.lname}  onChange={handleForm}/>
        </p>
        <p>
          <input type="text" name="phone"  value={contact.phone}  onChange={handleForm}/>
        </p>

        <p>
          <button type="submit">add contact</button>
        </p>
      </form>
    </React.Fragment>
  )
}

export default ContactForm
